/* TOPAPPBAR */
.top-app-bar {
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  position: fixed;
  width: 100%;
  z-index: 4;
}
.top-app-bar.fixed {
  transition: box-shadow 0.2s linear;
}
.top-app-bar.fixed.scrolled {
  box-shadow: 0 2px 4px -1px rgba(0, 0, 0, 0.2), 0 4px 5px 0 rgba(0, 0, 0, 0.14), 0 1px 10px 0 rgba(0, 0, 0, 0.12);
}
.top-app-bar a.icon {
  align-items: center;
  background-color: transparent;
  border: none;
  box-sizing: border-box;
  color: #fff;
  color: inherit;
  cursor: pointer;
  display: flex;
  fill: currentColor;
  flex-shrink: 0;
  height: 48px;
  justify-content: center;
  outline: none;
  padding: 12px;
  position: relative;
  text-decoration: none;
  transition: all 200ms ease-in-out;
  width: 48px;
}
.top-app-bar a.icon:hover {
  border-radius: 50%;
  background-color: #003869;
}
.top-app-bar a.icon:active {
  background-color: #001F3F;
  color: #01ff70;
}
.top-app-bar .row {
  box-sizing: border-box;
  display: flex;
  height: 64px;
  position: relative;
  width: 100%;
}
.top-app-bar section,
.top-app-bar .section {
  align-items: center;
  display: inline-flex;
  flex: 1 1 auto;
  min-width: 0;
  padding: 8px 12px;
  z-index: 1;
}
.top-app-bar .title {
  font-family: 'Roboto', sans-serif;
  font-size: 1.25rem;
  font-weight: 400;
  letter-spacing: 0.0125em;
  line-height: 2rem;
  overflow: hidden;
  padding-left: 20px;
  padding-right: 0;
  text-decoration: inherit;
  text-overflow: ellipsis;
  text-transform: inherit;
  white-space: nowrap;
  z-index: 1;
}
main,
.main {
  padding: 64px 10px;
}
